// export default ProductForm

import React, { Component } from 'react';

const RESET_VALUES = {
  productid: '',
  category: '',
  price: '',
  name: '',
  instock: true
};

class ProductForm extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSave = this.handleSave.bind(this);
    this.state = {
      product: Object.assign({}, RESET_VALUES),
      errors: {}
    };
  }

  handleChange(e) {
    const target = e.target;
    const name = target.name;
    const value = name === 'instock' ? target.checked : target.value;

    this.setState((prevState) => ({
      product: {
        ...prevState.product,
        [name]: value
      }
    }));
  }

  handleSave(e) {
    e.preventDefault();

    // Wrap in outer object so it matches { product: { ... } }
    this.props.onSave({ product: this.state.product });

    // Reset the form
    this.setState({
      product: Object.assign({}, RESET_VALUES),
      errors: {}
    });
  }

  render() {
    return (
      <form>
        <h4>Add a new product</h4>

        <p>
          <label>
            Name
            <br />
            <input
              type="text"
              className="form-control"
              name="name"
              onChange={this.handleChange}
              value={this.state.product.name}
            />
          </label>
        </p>

        <p>
          <label>
            Category
            <br />
            <input
              type="text"
              className="form-control"
              name="category"
              onChange={this.handleChange}
              value={this.state.product.category}
            />
          </label>
        </p>

        <p>
          <label>
            Price
            <br />
            <input
              type="text"
              className="form-control"
              name="price"
              onChange={this.handleChange}
              value={this.state.product.price}
            />
          </label>
        </p>

        <p>
          <label>
            In Stock
            <br />
            <input
              type="checkbox"
              name="instock"
              checked={this.state.product.instock}
              onChange={this.handleChange}
            />
          </label>
        </p>

        <input
          type="submit"
          className="btn btn-info"
          value="Save"
          onClick={this.handleSave}
        />
      </form>
    );
  }
}

export default ProductForm;
